component.sample:

$Version=2$
myreport "system/report"
	{
	minsize = 1;  // a parameter.
	title = "This is a hello-world report";

	comment "report/comment"
		{
		text = "Hello World";
		}
	}


$Version=2$
myComment "report/comment"
	{
	xpos = 10;
	text = "This is printed starting at column number 10.";
	}


$Version=2$
myColumn "report/column"
	{
	relypos = 5; // five lines down from top of current section
	style = bold,italic;
	source = myQuery,modified_date;
	dateformat = "MMM ddd, yyyy";
	}


$Version=2$
mySection "report/section"
	{
	title = "The Next Section";
	columns = 2;
	colsep = 2;       // 39 char wide cols on an 80 col page
	nullformat = "";  // display nothing if data is null instead of displaying 'NULL'

	//
	// more components go here
	//
	}


$Version=2$
normalForm "report/form"
	{
	source=myQuery;
	rulesep=yes;

	//
	// query data is printed here with report/data, report/column, etc.
	//
	}
outerForm "report/form"
	{
	source=myQuery;
	mode=outer;
	comment "report/comment" { text="Here are up to six of the records:"; }
	innerForm "report/form"
		{
		source=myQuery;
		mode=inner;
		reclimit=6;

		//
		// display query data here with report/data, report/column, etc.
		//
		}
	}


$Version=2$
myTable "report/table"
	{
	source=myQuery;
	expressions=yes;
	colnames='Name','Size';
	columns=':myQuery:name', 'condition(:myQuery:size == 0, \'empty\', :myQuery:size)';
	align=left,right;
	headeralign=left,left;
	titlebar=yes;
	}


$Version=2$
myQuery "report/query"
	{
	sql = "select :name, :size from /samples where :size >= &minsize";
	totalsize "report/aggregate" { compute = "sum(:size)"; }
	bigfilesizetotal "report/aggregate" { compute = "sum(:size)"; where=":size > 10000"; }
	}
