	{
	value = 0;
	}


$Version=2$
// This is a repeat widget inside a vbox
	vbox "widget/vbox"
	    {
	    x=10; y=10; cellsize=20; spacing=0;
	    rpt "widget/repeat"
		{
		sql="SELECT :name FROM /samples WHERE substring(:name,1,1)='t'";
		button "widget/button"
		    {
		    type="text";
		    width=100; height=20;
		    text=runserver(:rpt:name);
		    }
		}
	    }
